mapper.select:
SELECT * FROM "IMAGE"
WHERE BOARD_NO=#{boardNo}
ORDER BY IMG_ORDER DESC

mapper.insert:
INSERT INTO "IMAGE" SELECT SEQ_IMG_NO.NEXTVAL, A.* FROM (
SELECT #{img.imgPath} IMG_PATH, #{img.imgReName} IMG_RENAME, #{img.imgOriginal} IMG_ORGINAL, #{img.imgOrder} IMG_ORDER, #{img.boardNo} BOARD_NO FROM DUAL
)A
