mapper.select:
SELECT * FROM "IMAGE"
WHERE BOARD_NO=#{boardNo}
ORDER BY IMG_ORDER DESC

mapper.insert:
INSERT INTO "IMAGE" SELECT SEQ_IMG_NO.NEXTVAL, A.* FROM (
SELECT #{img.imgPath} IMG_PATH, #{img.imgReName} IMG_RENAME, #{img.imgOriginal} IMG_ORGINAL, #{img.imgOrder} IMG_ORDER, #{img.boardNo} BOARD_NO FROM DUAL
)A

mapper.update:
UPDATE "BOARD" SET
"DELETE" = 'Y'
WHERE MEMBER_NO =#{memberNo}
AND BOARD_NO =#{boardNo}

mapper.delete:
DELETE FROM "IMAGE"
WHERE BOARD_NO = #{boardNo}
AND IMG_ORDER IN(${deleteList})

mapper.update:
UPDATE "IMAGE" SET
IMG_PATH = #{imgPath},
IMG_ORIGINAL = #{imgOriginal},
IMG_RENAME = #{imgReName}
WHERE BOARD_NO = #{boardNo}
AND IMG_ORDER = #{imgOrder}
